import HelloWorld from './components/HelloWorld.vue';
import CountriesStep from "./components/CountriesStep.vue";
const COUNTRIES_CSV = "./assets/data/productioncountries.csv";
const MAX_SVG_WIDTH = 600;
export default {
  name: 'App',
  components: {
    HelloWorld,
    CountriesStep
  },

  data() {
    return {
      countries: null,
      width: MAX_SVG_WIDTH
    };
  },

  // computed: {
  // },
  methods: {
    onResize() {
      this.width = Math.min(MAX_SVG_WIDTH, window.innerWidth);
    }

  },

  mounted() {
    fetch(COUNTRIES_CSV).then(data => {
      this.countries = data;
      console.log(data);
    });
    window.addEventListener("resize", this.onResize);
  },

  beforeUnmount() {
    window.removeEventListener("resize", this.onResize);
  }

};
